import React, { useState } from "react";
import {
  View,
  Text,
  FlatList,
  TouchableOpacity,
  StyleSheet,
  Image,
  TextInput,
  Alert,
} from "react-native";
import { useRouter } from "expo-router";
import * as ImagePicker from "expo-image-picker";
import { COLORS } from "../../constants/theme";

const Items = () => {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItemName, setNewItemName] = useState("");
  const [newItemDetails, setNewItemDetails] = useState("");
  const [newSerialNumber, setNewSerialNumber] = useState("");
  const [newLocation, setNewLocation] = useState("");
  const [newCategory, setNewCategory] = useState("");
  const [newStatus, setNewStatus] = useState("Available");

  // Initial data moved inside component
  const [items, setItems] = useState([
    {
      id: "1",
      name: "Lenovo IdeaPad Slim 3 14IHRIO",
      details:
        "Intel Core i5 13420H, 16GB DDR5 4800 (Up to 24GB Support), 512GB SSD",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: "2",
      name: "DELL G16 7630",
      details:
        'Intel Core i9-13900HX | RTX 4060 8GB | 16GB RAM 1TB SSD | 16" WQXGA 240Hz Display | Windows 11 Pro + Office 2021',
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: "3",
      name: "HP OMEN 16-B Gaming",
      details:
        'Intel Core i7-11800H | RTX 3050 | 16GB DDR4 | 512GB SSD | 16.1" FHD Display | Windows 11',
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: "4",
      name: "Predator Pro M612 PRO RGB Wireless Mouse",
      details:
        "An ergonomic, right-handed gaming mouse with up to 8000 DPI, multiple connection options (Bluetooth, 2.4 GHz wireless, and wired USB-C), and nine programmable buttons",
      image:
        "data:image/jpeg;base64,",
    },
  ]);

  const pickImage = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.5,
    });

    if (!result.canceled) {
      setSelectedImage(result.assets[0].uri);
    }
  };

  const [selectedImage, setSelectedImage] = useState(null);
  const [editingItem, setEditingItem] = useState(null);

  const deleteItem = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  const editItem = (item) => {
    setEditingItem(item);
    setNewItemName(item.name);
    setNewItemDetails(item.details);
    setNewSerialNumber(item.serialNumber || "");
    setNewLocation(item.location || "");
    setNewCategory(item.category || "");
    setNewStatus(item.status || "Available");
    setSelectedImage(item.image);
    setShowAddForm(true);
  };

  const updateItem = () => {
    if (newItemName.trim() && newItemDetails.trim()) {
      const updatedItem = {
        ...editingItem,
        name: newItemName,
        details: newItemDetails,
        image: selectedImage || "https://via.placeholder.com/150",
        serialNumber:
          newSerialNumber || "SN" + Math.random().toString().substr(2, 8),
        location: newLocation || "Storage Room A",
        category: newCategory || "Electronics",
        status: newStatus,
      };
      setItems(
        items.map((item) => (item.id === editingItem.id ? updatedItem : item))
      );
      setEditingItem(null);
      setNewItemName("");
      setNewItemDetails("");
      setNewSerialNumber("");
      setNewLocation("");
      setNewCategory("");
      setNewStatus("Available");
      setSelectedImage(null);
      setShowAddForm(false);
    }
  };

  const addItem = () => {
    if (newItemName.trim() && newItemDetails.trim()) {
      const newItem = {
        id: (items.length + 1).toString(),
        name: newItemName,
        details: newItemDetails,
        image: selectedImage || "https://via.placeholder.com/150",
        serialNumber:
          newSerialNumber || "SN" + Math.random().toString().substr(2, 8),
        location: newLocation || "Storage Room A",
        category: newCategory || "Electronics",
        status: newStatus,
        dateAdded: new Date().toISOString().split("T")[0],
      };
      setItems([newItem, ...items]);
      setNewItemName("");
      setNewItemDetails("");
      setNewSerialNumber("");
      setNewLocation("");
      setNewCategory("");
      setNewStatus("Available");
      setSelectedImage(null);
      setShowAddForm(false);
    }
  };

  const filteredData = items.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity
          style={styles.backButton}
          onPress={() => router.push("/home_screen/home")}
        >
          <Text style={styles.backArrow}>‚Üê</Text>
        </TouchableOpacity>
        <Image
          source={require("../../assets/icon.png")}
          style={styles.headerIcon}
        />
        <Text style={styles.headerTitle}>ITEMS</Text>
        <Image
          source={require("../../assets/splash-icon.png")}
          style={styles.rightIcon}
        />
      </View>

      <View style={styles.searchContainer}>
        <Image
          source={{
            uri: "https://cdn-icons-png.flaticon.com/512/622/622669.png",
          }}
          style={styles.searchIcon}
        />
        <TextInput
          placeholder="Search ..."
          placeholderTextColor="#888"
          style={styles.searchInput}
          value={search}
          onChangeText={setSearch}
        />
        <TouchableOpacity
          style={styles.addButton}
          onPress={() => setShowAddForm(true)}
        >
          <Text style={styles.addButtonText}>+</Text>
        </TouchableOpacity>
      </View>

      {showAddForm && (
        <View style={styles.addForm}>
          <TextInput
            placeholder="Item Name"
            placeholderTextColor="#888"
            style={styles.addInput}
            value={newItemName}
            onChangeText={setNewItemName}
          />
          <TextInput
            placeholder="Item Details"
            placeholderTextColor="#888"
            style={styles.addInput}
            value={newItemDetails}
            onChangeText={setNewItemDetails}
          />
          <TextInput
            placeholder="Serial Number"
            placeholderTextColor="#888"
            style={styles.addInput}
            value={newSerialNumber}
            onChangeText={setNewSerialNumber}
          />
          <TextInput
            placeholder="Location"
            placeholderTextColor="#888"
            style={styles.addInput}
            value={newLocation}
            onChangeText={setNewLocation}
          />
          <TextInput
            placeholder="Category"
            placeholderTextColor="#888"
            style={styles.addInput}
            value={newCategory}
            onChangeText={setNewCategory}
          />
          <TouchableOpacity style={styles.imageButton} onPress={pickImage}>
            <Text style={styles.imageButtonText}>üì∑ Add Image</Text>
          </TouchableOpacity>
          <View style={styles.addButtons}>
            <TouchableOpacity
              style={styles.saveButton}
              onPress={editingItem ? updateItem : addItem}
            >
              <Text style={styles.buttonText}>
                {editingItem ? "Update" : "Save"}
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.cancelButton}
              onPress={() => setShowAddForm(false)}
            >
              <Text style={styles.buttonText}>Cancel</Text>
            </TouchableOpacity>
          </View>
        </View>
      )}

      <FlatList
        data={filteredData}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.itemCard}
            onPress={() =>
              router.push({
                pathname: "items_screens/item-details",
                params: {
                  id: item.id,
                  name: item.name,
                  details: item.details,
                  image: item.image,
                },
              })
            }
          >
            <Image source={{ uri: item.image }} style={styles.itemImage} />
            <View style={styles.itemText}>
              <Text style={styles.itemName}>{item.name}</Text>
              <Text style={styles.itemDetails}>{item.details}</Text>
            </View>
            <View style={styles.actionButtons}>
              <TouchableOpacity
                style={styles.editButton}
                onPress={(e) => {
                  e.stopPropagation();
                  editItem(item);
                }}
              >
                <Text style={styles.editButtonText}>‚úèÔ∏è</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.deleteButton}
                onPress={(e) => {
                  e.stopPropagation();
                  deleteItem(item.id);
                }}
              >
                <Text style={styles.deleteButtonText}>üóëÔ∏è</Text>
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        )}
        contentContainerStyle={{ paddingBottom: 20 }}
      />
    </View>
  );
};

